<?php if (!defined('BASEPATH')) exit('No direct script access allowed');class External_controller extends CI_Controller{	public function __construct()	{		parent::__construct();		$this->is_logged();	}	public function is_logged()	{		if ($this->session->userdata('admin_id') == "") {			redirect('erp-access');		}	}	public function check_access()	{		if ($this->session->userdata('admin_id') != "1") {			$access = $this->Setting_model->get_user_privilege_link();			if (!in_array($this->uri->segment(1), $access)) {				$this->session->set_flashdata('message', 'Sorry! You dont have access to this module!');				redirect(base_url());			}		}	}	public function add_external_verification_user()	{		$this->form_validation->set_rules('name', 'name', 'required');		if ($this->form_validation->run() === FALSE) {			$data['single'] = $this->External_model->get_single_user();			$this->load->view('admin/add_external_verification_user', $data);		} else {			$res = $this->External_model->set_verification_user();			if ($res == 1) {				$this->session->set_flashdata("success", "User added successfully!");			} else {				$this->session->set_flashdata('sucess', 'User updated successfully!!');			}			redirect('external_verification_user');		}	}	public function external_verification_user()	{		$this->load->view('admin/external_verification_user');	}	public function external_verification_student_list()	{		$this->load->view('admin/external_verification_student_list');	}	public function external_verification_student_rejected_list()	{		$this->load->view('admin/external_verification_student_rejected_list');	}	public function view_verify_details()	{		if ($this->input->post('re_ini') == "re_ini") {			$this->External_model->set_re_verification();			$this->session->set_flashdata('sucess', 'Student initiated for re verify successfully!!');			redirect($_SERVER['HTTP_REFERER']);		}		$data['admission'] = $this->External_model->get_single_student();		$data['verified'] = $this->External_model->get_single_student_verified();		$data['qualification'] = $this->External_model->get_student_qualification();		$this->load->view('admin/view_verify_details', $data);	}	public function external_blended_verification_student_list()	{		$this->load->view('admin/external_blended_verification_student_list');	}	public function external_blended_verification_student_rejected_list()	{		$this->load->view('admin/external_blended_verification_student_rejected_list');	}	public function view_blended_verify_details()	{		if ($this->input->post('re_ini') == "re_ini") {			$this->External_model->set_blended_re_verification();			$this->session->set_flashdata('sucess', 'Student initiated for re verify successfully!!');			redirect($_SERVER['HTTP_REFERER']);		}		$data['admission'] = $this->External_model->get_single_blended_student();		$data['verified'] = $this->External_model->get_single_blended_student_verified();		$this->load->view('admin/view_blended_verify_details', $data);	}}