<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed'); class Quiz_controller extends CI_Controller {	public function __construct(){		parent::__construct();	}	public function quiz_login(){		if($this->input->server('REQUEST_METHOD') == 'GET'){			$this->load->view('exam/quiz_login');		}else{			$data = $this->Quiz_model->check_student_login();			// echo"<pre>";print_r($data);exit;			if($data){				redirect("exam_dashboard");        			}else{				$this->session->set_flashdata('message','Invalid Login Credentials!');				redirect("phd_exam_login");			}		}	}		public function exam_dashboard(){		if($this->session->userdata('student_email') == ""){			redirect('phd_exam_login');		}		$student_email = $this->session->userdata('student_email');		if($this->input->server('REQUEST_METHOD') == 'GET'){			$data['student_email'] = $student_email;			$data['exam_list'] = $this->Quiz_model->get_examination_list();			$this->load->view("exam/dashboard", $data);		}	}	public function show_exam(){		if($this->session->userdata('student_email') == ""){			redirect('phd_exam_login');		}		$student_email = $this->session->userdata('student_email');		if($this->input->server('REQUEST_METHOD') == 'GET' and $student_email){			$data['question_paper'] = $this->Quiz_model->get_question_paper();			// echo "<pre>";print_r($data['question_paper']);exit;			$data['student_email'] = $student_email;			$data['test_title'] = $this->Quiz_model->get_test_title();			$this->load->view('exam/show_quiz', $data);		}		else if($this->input->server('REQUEST_METHOD') == 'POST'){			$this->Quiz_model->save_test_answers($this->uri->segment(2));			redirect('thankyou');		}else{			redirect("phd_exam_login");		}	}	public function exam_logout(){		$this->session->sess_destroy();		redirect('phd_exam_login');	}	public function thankyou(){	    $this->load->view('exam/thankyou');	}}