<?php include('header.php'); ?><style>    /* Main Container Styling */    .video-container {        display: flex;        gap: 20px;        padding: 20px;    }    /* Video Player Styling */    .video-player-wrapper {        flex: 3;    }    .video-player {        width: 100%;        height: 500px;        border-radius: 10px;        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);    }    /* Playlist Styling */    .video-playlist {        flex: 1;        background: #fff;        border-radius: 10px;        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);        padding: 15px;        overflow-y: auto;        max-height: 540px;    }    .video-playlist-item {        display: flex;        align-items: center;        margin-bottom: 15px;        padding: 10px;        background: #f9f9f9;        border-radius: 8px;        cursor: pointer;        transition: all 0.3s ease;    }    .video-playlist-item:hover {        background: #c31432;        color: #fff;        transform: translateY(-2px);        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);    }    .video-playlist-item.active {        background: #c31432 !important;        color: #fff;    }    .video-thumbnail {        width: 100px;        height: 60px;        border-radius: 5px;        margin-right: 10px;        object-fit: cover;    }    .video-title {        font-size: 14px;        font-weight: 500;    }    /* Responsive Design */    @media (max-width: 768px) {        .video-container {            flex-direction: column;        }        .video-player {            height: 300px;        }        .video-playlist {            max-height: 300px;        }    }</style><div class="container-fluid page-body-wrapper">    <div class="main-panel">        <div class="content-wrapper">            <div class="row">                <div class="col-md-12 grid-margin stretch-card">                    <div class="card">                        <div class="card-header custom-header">                            <h4 class="card-title">ERP Video Manual</h4>    