      playlistItems.forEach(i => i.classList.remove('active'));            // Add active class to the clicked item            this.classList.add('active');            // Get the video source from the clicked item            const videoSource = this.getAttribute('data-video');            // Check if the video source is valid            if (videoSource && videoSource !== '#') {                videoPlayer.setAttribute('src', videoSource);                videoPlayer.load();                videoPlayer.play();            } else {                alert('This video is not available yet.');            }        });    });});</script><?php include('footer.php'); ?>