m").validate({     ignore: ":hidden:not(select)",	rules: {		student_name: {			required: true,			noHTMLtags: true,		},		study_mode: {			required: true,			noHTMLtags: true,		}, 		                <?php if (!empty($local_data) && $local_data->signature == "") { ?>            old_signature :{                required: true,                noHTMLtags: true,            },        <?php }else if(empty($local_data)){ ?>            old_signature :{                required: true,                noHTMLtags: true,            },        <?php } ?>	},	messages: {		student_name: {			required: "Please enter Full name!",			noHTMLtags: "HTML tags not allowed!",		},		study_mode: {			required: "Please select study mode!",			noHTMLtags: "HTML tags not allowed!",		},        <?php if (!empty($local_data) && $local_data->signature == "") { ?>            old_signature :{                required: "Please select signature!",                noHTMLtags: "HTML tags not allowed!",            },         <?php }else if(empty($local_data)){ ?>            old_signature :{                required: "Please select signature!",                noHTMLtags: "HTML tags not allowed!",            },         <?php } ?>	}, 	submitHandler: function(form){		form.submit();	} });$('#study_mode').change(function () {    $('#study_mode').valid();});function deleteRow(id) {    if(confirm("Are you sure you want to delete this?")){         $.ajax({            type: "POST",            url: "<?=base_url();?>admin/Ajax_controller/remove_unused_setup_details",            data: {                'id': id,            },            success: function(data) {                    $("#removable_details_"+id).remove();                         },            error: function(jqXHR, textStatus, errorThrown) {                console.log(textStatus, errorThrown);            }        });    }}var i = <?php echo $i; ?>;function createAddMoreFields() {    let appedData = `<tr>                        <input type="hidden" value="${i}" name="indices[]">                        <td>                            <input type="text" name="subject_name_${i}[]" id="subject_name_${i}" class="form-control" value="">                        </td>                        <td>                            <input type="text" name="no_of_hours_study_${i}[]" id="no_of_hours_study_${i}" class="form-control" value="">                        </td>                        <td>                            <input type="text" name="no_of_hours_subject_${i}[]" id="no_of_hours_subject_${i}" class="form-control" value="">                        </td>                        <td>                            <input type="text" name="grade_${i}[]" id="grade_${i}" class="form-control" value="">                        </td>                        <td>                            <span onclick="removeRow(this)" class="delete_area"><i class="fa fa-close" style="color:red;margin-top:35px;" aria-hidden="true"></i></span>                        </td>                    </tr>`;    $('#add_more_div').append(appedData);    i++;}function removeRow(arg) {    $(arg).parent().parent().remove();}    </script> 