ML tags not allowed!"},					}, 	submitHandler: function(form){		form.submit();	} });$("#payment_form").validate({	ignore: ":hidden:not(select)",	rules: {		name: {required:true,noHTMLtags:true}, 		email: {required:true,email:true,noHTMLtags:true}, 		mobile_number: {required:true,number:true,minlength:10,maxlength:10,noHTMLtags:true}, 		course: {required:true,noHTMLtags:true}, 		year_sem: {required:true,noHTMLtags:true}, 		pay_for: {required:true,noHTMLtags:true}, 		date_of_payment: {required:true,noHTMLtags:true}, 		amount: {required:true,number:true,noHTMLtags:true}, 		payment_mode: {required:true,noHTMLtags:true}, 		transaction_id: {required:true,noHTMLtags:true}, 		collected_by: {required:true,noHTMLtags:true}, 		address: {required:true,noHTMLtags:true}, 	},	messages: {		name: {required:"Please enter name",noHTMLtags:"HTML tags not allowed!"},	 		email: {required:"Please enter email",email:"Please enter valid email",noHTMLtags:"Please enter valid value"}, 		mobile_number: {required:"Please enter mobile number",number:"Please enter valid number",minlength:"Please enter 10 digit mobile number",maxlength:"Please enter 10 digit mobile number",noHTMLtags:true}, 		course: {required:"Please select course name",noHTMLtags:"Please enter name"}, 		year_sem: {required:"Please select year/sem",noHTMLtags:"Please enter name"}, 		pay_for: {required:"Please select payment reason",noHTMLtags:"Please enter name"}, 		date_of_payment: {required:"Please select payment date",noHTMLtags:"Please enter valid value"}, 		amount: {required:"Please enter amount",number:"Please enter only number",noHTMLtags:"Please enter valid value"}, 		payment_mode: {required:"Please select payment mode",noHTMLtags:"Please enter valid value"}, 		transaction_id: {required:"Please enter transaction id",noHTMLtags:"Please enter valid value"}, 		collected_by: {required:"Please enter collected by",noHTMLtags:"Please enter valid value"}, 		address: {required:"Please enter address",noHTMLtags:"Please enter valid value"}, 	}, 	submitHandler: function(form){		form.submit();	} });$("#course").change(function() {    $("#course").valid();});$("#year_sem").change(function() {    $("#year_sem").valid();});$("#pay_for").change(function() {    $("#pay_for").valid();});$("#payment_mode").change(function() {    $("#payment_mode").valid();});$("#registration_number").keyup(function(){  	$.ajax({		type: "POST",		url: "<?=base_url();?>web/Web_controller/get_student_data",		data:{'registration_number':$("#registration_number").val()},		success: function(data){			var opts = $.parseJSON(data);			 console.log(opts);			 if(opts){				$("#name").val(opts['student_name']);				$("#mobile_number").val(opts['mobile']);				$("#email").val(opts['email']);				$("#address").val(opts['address']); 			 }else{				$("#name").val('');				$("#mobile_number").val('');				$("#email").val('');				$("#address").val(''); 			 }			 		},		 error: function(jqXHR, textStatus, errorThrown) {		   console.log(textStatus, errorThrown);		}	});	}); </script><script>$(document).ready(function() {      $('#payment_mode').on('change', function() {        var selectedMode = $(this).val();        if (selectedMode === 'Cash') {            $('#transaction_id').closest('.form-group').hide();            $('#transaction_id').removeAttr('required');        } else {            $('#transaction_id').closest('.form-group').show();            $('#transaction_id').attr('required', 'required');        }    });});</script>