<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed'); class Course_work_controller extends CI_Controller {	public function __construct(){		parent::__construct();	}	public function course_work_login(){		if($this->input->server('REQUEST_METHOD') == 'GET'){			$this->load->view('course_work/course_work_login');		}else{			$data = $this->Course_work_model->check_couse_work_login();			if($data){				redirect("course_work_dashboard");        			}else{				$this->session->set_flashdata('message','Invalid Login Credentials!');				redirect("course_work_login");			}		}	}		public function course_work_dashboard(){		if($this->session->userdata('student_email') == ""){			redirect('course_work_login');		}		$student_email = $this->session->userdata('student_email');		if($this->input->server('REQUEST_METHOD') == 'GET'){			$data['student_email'] = $student_email;			$data['exam_list'] = $this->Course_work_model->get_course_work_examination_list();			$data['exam_list_appeared'] = $this->Course_work_model->get_appeared_course_work_exam_list();			$this->load->view("course_work/course_work_dashboard", $data);		}	}	public function course_work_logout(){		$this->session->sess_destroy();		redirect('course_work_login');	}	public function my_course_work_exam(){		if($this->session->userdata('student_email') == ""){			redirect('course_work_login');		}		$student_email = $this->session->userdata('student_email');		if($this->input->server('REQUEST_METHOD') == 'GET' and $student_email){			$data['question_paper'] = $this->Course_work_model->get_question_paper();			$data['student_email'] = $student_email;			$data['test_title'] = $this->Course_work_model->get_test_title();			$data['test_time'] = $this->Course_work_model->get_test_time();			$this->load->view('course_work/course_work_exam', $data);		}		else if($this->input->server('REQUEST_METHOD') == 'POST'){			$this->Course_work_model->submit_save_test_answers($this->uri->segment(2));			redirect('thankyou-submission');		}else{			redirect("course_work_login");		}	}	public function thankyou_submission(){	    $this->load->view('admin/thankyou_submission');	}	// public function exam_logout(){	// 	$this->session->sess_destroy();	// 	redirect('phd_exam_login');	// }	// public function thankyou(){	//     $this->load->view('exam/thankyou');	// }}