<?php defined('BASEPATH') OR exit('No direct script access allowed');class Mail_read_controller extends CI_Controller {	public function __construct(){		parent::__construct(); 		$this->load->model('web/Mail_read_model');	}			public function read_mail(){ 		$data['mail'] = $this->Mail_read_model->get_read_mail();   		$this->load->view('landing_page/read_mail',$data); 	} }