<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed'); class Common_print_model extends CI_Model { 	public function get_admission_form($student_id){ 		$this->db->select('tbl_student.*,tbl_id_management.id_name,countries.name as country_name,states.name as state_name,cities.name as city_name,tbl_session.session_name,tbl_faculty.faculty_name,tbl_course.course_name,tbl_course.print_name,tbl_stream.stream_name,tbl_course_type.course_type as course_type_name,tbl_center.center_name'); 		$this->db->where('tbl_student.is_deleted','0'); 		//$this->db->where('tbl_student.admission_status !=','0'); 		$this->db->where('tbl_student.id',$student_id); 		$this->db->join('tbl_course_type','tbl_course_type.id = tbl_student.course_type','left'); 		$this->db->join('tbl_course','tbl_course.id = tbl_student.course_id','left'); 		$this->db->join('tbl_stream','tbl_stream.id = tbl_student.stream_id','left'); 		$this->db->join('tbl_faculty','tbl_faculty.id = tbl_student.faculty_id','left'); 		$this->db->join('tbl_session','tbl_session.id = tbl_student.session_id','left'); 		$this->db->join('tbl_id_management','tbl_id_management.id = tbl_student.id_type','left'); 		$this->db->join('tbl_center','tbl_center.id = tbl_student.center_id','left'); 		$this->db->join('countries','countries.id = tbl_student.country','left'); 		$this->db->join('states','states.id = tbl_student.state','left'); 		$this->db->join('cities','cities.id = tbl_student.city','left'); 		$result = $this->db->get('tbl_student'); 		return $result->row();	}	public function get_admission_qualification($student_id){		$this->db->where('is_deleted','0');		$this->db->where('student_id',$student_id);		$result = $this->db->get('tbl_student_qualification');		return $result->row();	}	public function get_sep_id_student($student_id){ 		$this->db->select('tbl_separate_student.*,tbl_faculty.faculty_name,tbl_course.course_name,tbl_course.print_name,tbl_stream.stream_name');		$this->db->where('tbl_separate_student.id',$student_id);		$this->db->join('tbl_course','tbl_course.id = tbl_separate_student.course_id');		$this->db->join('tbl_stream','tbl_stream.id = tbl_separate_student.stream_id');		$this->db->join('tbl_faculty','tbl_faculty.id = tbl_separate_student.faculty_id');		$result = $this->db->get('tbl_separate_student');		return $result->row();	}	public function get_single_marksheet($marksheet_id){		$this->db->select('tbl_separate_student_marksheet.*,tbl_separate_student_exam_results.result,tbl_separate_student_exam_results.examination_month,tbl_separate_student_exam_results.examination_year,tbl_separate_student.student_name,tbl_separate_student.father_name,tbl_separate_student.date_of_birth,tbl_separate_student.mother_name,tbl_course.course_name,tbl_course.id as course_id,tbl_stream.stream_name,tbl_session.session_name,tbl_session.pre_session');		$this->db->where('tbl_separate_student_marksheet.is_deleted','0');		$this->db->where('tbl_separate_student_marksheet.id',$marksheet_id);		$this->db->join('tbl_separate_student_exam_results','tbl_separate_student_exam_results.id = tbl_separate_student_marksheet.result_id');		$this->db->join('tbl_separate_student','tbl_separate_student.id = tbl_separate_student_marksheet.student_id');		$this->db->join('tbl_course','tbl_course.id = tbl_separate_student.course_id');		$this->db->join('tbl_stream','tbl_stream.id = tbl_separate_student.stream_id');		$this->db->join('tbl_session','tbl_session.id = tbl_separate_student.session_id');		$result = $this->db->get('tbl_separate_student_marksheet');		return $result->row();	}	public function get_migration_certificate($id){ 		$this->db->select("tbl_separate_student_migration.*,tbl_stream.stream_name,tbl_separate_student.student_name,tbl_separate_student.year_sem,tbl_separate_student.father_name,tbl_separate_student.enrollment_number,tbl_course.course_name,tbl_course.sort_name,tbl_session.session_start_date"); 		$this->db->where("tbl_separate_student_migration.is_deleted","0");		$this->db->where('tbl_separate_student_migration.payment_status',"1"); 		$this->db->where("tbl_separate_student_migration.id",$id); 		$this->db->join("tbl_separate_student","tbl_separate_student.id = tbl_separate_student_migration.student_id"); 		$this->db->join("tbl_course","tbl_course.id = tbl_separate_student.course_id");  		$this->db->join("tbl_stream","tbl_stream.id = tbl_separate_student.stream_id");  		$this->db->join("tbl_session","tbl_session.id = tbl_separate_student.session_id");  		$result = $this->db->get("tbl_separate_student_migration")->row(); 		return $result;	}}