<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed'); class Alumni_model extends CI_Model { 	public function get_alumni_login(){  		$_GET = $_POST;		$this->db->where('is_deleted','0');		$this->db->where('status','1');		$this->db->where('hold_login','0');		$this->db->where('admission_status','1');		$this->db->where('username',$this->input->post('username'));		$this->db->where('password',$this->input->post('password'));		$result = $this->db->get('tbl_separate_student');		$result = $result->row(); 		echo json_encode($result);	}	public function get_my_alumni_profile(){  		$_GET = $_POST;		$this->db->where('is_deleted','0');		$this->db->where('status','1');		$this->db->where('id',$this->input->post('id')); 		$result = $this->db->get('tbl_separate_student');		$result = $result->row();		if($result->photo !=""){			$result->photo_path = $this->Digitalocean_model->get_photo('images/separate_student/photo/'.$result->photo);		}else{			$result->photo_path = "";		} 		echo json_encode($result);	}	public function set_alumni_password_update(){		$_GET = $_POST; 		$data = array(			'password' 	=> $this->input->post('new_password'), 		);	 		 		$this->db->where('id',$this->input->post('id')); 		$result = $this->db->update('tbl_separate_student',$data);	}	public function set_without_enroll_video_kyc(){		$_GET = $_POST; 		$data = array(			'name' 		=> $this->input->post('name'), 			'mobile_number' 		=> $this->input->post('mobile_number'), 			'video' 	=> $this->input->post('video'), 			'created_on' 	=> $this->input->post('created_on'), 		);	 		 		$this->db->where('id',$this->input->post('id')); 		$result = $this->db->insert('tbl_direct_video_kyc',$data);		return true;	}	public function get_alumni_video_kyc(){		$_GET = $_POST; 		$data = array(			'enrollment_number' 	=> $this->input->post('enrollment_number'), 			'video' 	=> $this->input->post('video'), 			'created_on' 	=> $this->input->post('created_on'), 		);	 		 		$this->db->where('id',$this->input->post('id')); 		$result = $this->db->insert('tbl_blended_video_kyc',$data);		return true;	}	public function get_alumni_admission_form(){		$_GET = $_POST; 		$this->db->select('tbl_separate_student.*,tbl_session.session_name,tbl_faculty.faculty_name,tbl_course.print_name,tbl_stream.stream_name,tbl_course_type.course_type as course_type_name,tbl_center.center_name, tbl_course_stream_relation.year_duration as duration');		$this->db->where('tbl_separate_student.is_deleted','0');		$this->db->where('tbl_separate_student.id',$this->input->post('id')); 		$this->db->join('tbl_course_type','tbl_course_type.id = tbl_separate_student.course_type');		$this->db->join('tbl_course','tbl_course.id = tbl_separate_student.course_id');		$this->db->join('tbl_stream','tbl_stream.id = tbl_separate_student.stream_id');		$this->db->join('tbl_faculty','tbl_faculty.id = tbl_separate_student.faculty_id');		$this->db->join('tbl_session','tbl_session.id = tbl_separate_student.session_id'); 		$this->db->join('tbl_center','tbl_center.id = tbl_separate_student.center_id'); 		$this->db->join('tbl_course_stream_relation','tbl_separate_student.course_id = tbl_course_stream_relation.course'); 		$result = $this->db->get('tbl_separate_student');		$result = $result->row();		$result->id_header_logo = $this->Digitalocean_model->get_photo('images/logo/logo.png');		$result->id_back_logo = $this->Digitalocean_model->get_photo('images/logo/bir.png');		$result->default_photo = $this->Digitalocean_model->get_photo('images/profile_pic/default.jpg');		$result->photo_path = $this->Digitalocean_model->get_photo('images/separate_student/photo/'.$result->photo); 		echo json_encode($result);	}	public function get_alumni_provisional_registration_letter(){		$_GET = $_POST;  		$this->db->select("tbl_separate_student.*,tbl_stream.stream_name");		$this->db->where("tbl_separate_student.id",$this->input->post('id'));		$this->db->join("tbl_stream","tbl_stream.id=tbl_separate_student.stream_id");		$result = $this->db->get("tbl_separate_student");		$result = $result->row();		if(date("Y-m-d",strtotime($result->created_on)) <= "2022-02-19"){ 			$result->registrar_signature = 	$this->Digitalocean_model->get_photo('images/Deputy_Registrar.png');		}else{ 			$result->registrar_signature = 	$this->Digitalocean_model->get_photo('images/seprate_reg.PNG');		}		$result->header_logo_path = $this->Digitalocean_model->get_photo('images/BTU.jpeg');		echo json_encode($result);	}	public function get_alumni_marksheet(){		$_GET = $_POST;  		$this->db->where('is_deleted','0');		$this->db->where('status','1');		$this->db->where('id',$this->input->post('id')); 		$profile = $this->db->get('tbl_separate_student');		$profile = $profile->row(); 		if($profile->course_id == "23"){			$this->db->where('student_id',$this->input->post('id')); 			$this->db->where('status','1');			$this->db->where('is_deleted','0');			$this->db->order_by('id','DESC');			$result = $this->db->get('tbl_separate_student_course_work_result');			$result = $result->result();		}else{ 		    $this->db->select('tbl_separate_student_marksheet.*');			$this->db->where("tbl_separate_student_marksheet.is_deleted","0");			$this->db->where("tbl_separate_student_marksheet.status","1");			$this->db->where("tbl_separate_student_exam_results.is_deleted","0");			$this->db->where("tbl_separate_student_exam_results.status","1"); 			$this->db->where("tbl_separate_student_marksheet.student_id",$this->input->post('id'));			$this->db->join('tbl_separate_student_exam_results','tbl_separate_student_exam_results.id = tbl_separate_student_marksheet.result_id');			$result = $this->db->get("tbl_separate_student_marksheet");			 $result =$result->result();		}		echo json_encode($result);	}	public function get_almni_single_marksheet_result(){		$_GET = $_POST;		$this->db->select('tbl_separate_student_marksheet.*,tbl_separate_student_exam_results.result,tbl_separate_student_exam_results.examination_month,tbl_separate_student_exam_results.examination_year,tbl_separate_student.student_name,tbl_separate_student.father_name,tbl_separate_student.mother_name,tbl_separate_student.date_of_birth,tbl_course.course_name,tbl_course.id as course_id,tbl_stream.stream_name,tbl_session.session_name,tbl_session.pre_session');		$this->db->where('tbl_separate_student_marksheet.is_deleted','0');		$this->db->where('tbl_separate_student_marksheet.status','1');		$this->db->where('tbl_separate_student_exam_results.is_deleted','0');		$this->db->where('tbl_separate_student_marksheet.id',$this->input->post('marksheet_id'));		$this->db->where('tbl_separate_student.id',$this->input->post('id'));		$this->db->join('tbl_separate_student_exam_results','tbl_separate_student_exam_results.id = tbl_separate_student_marksheet.result_id');		$this->db->join('tbl_separate_student','tbl_separate_student.id = tbl_separate_student_marksheet.student_id');		$this->db->join('tbl_course','tbl_course.id = tbl_separate_student.course_id');		$this->db->join('tbl_stream','tbl_stream.id = tbl_separate_student.stream_id');		$this->db->join('tbl_session','tbl_session.id = tbl_separate_student.session_id');		$result = $this->db->get('tbl_separate_student_marksheet');		$result = $result->row();		$result->header_logo = $this->Digitalocean_model->get_photo('images/new_marksheet-background.png');		$result->footer_strip = $this->Digitalocean_model->get_photo('images/marksheet_footer_strip.png'); 		echo json_encode($result);	} 	public function get_almni_selected_subject_for_result(){		$_GET = $_POST;		$this->db->select('tbl_separate_student_examination_result_details.*,tbl_subject.subject_name,tbl_subject.subject_code,tbl_subject.subject_type');		$this->db->where('tbl_separate_student_examination_result_details.is_deleted','0');		$this->db->where('tbl_separate_student_examination_result_details.status','1');		$this->db->where('tbl_separate_student_examination_result_details.result_id',$this->input->post('result_id'));		$this->db->join('tbl_subject','tbl_subject.id = tbl_separate_student_examination_result_details.subject_id');		$this->db->order_by('tbl_subject.subject_code','ASC');		$result = $this->db->get('tbl_separate_student_examination_result_details');		$result = $result->result();		echo json_encode($result);	}		public function get_direct_image_path(){		$_GET = $_POST;		$outout = $this->Digitalocean_model->get_photo($this->input->post('path').$this->input->post('name')); 		echo json_encode($outout);			}	public function get_almni_coursework_single_marksheet_result(){		$_GET = $_POST; 		$this->db->select('tbl_separate_student_course_work_result.*,tbl_separate_student.student_name,tbl_separate_student.father_name,tbl_separate_student.enrollment_number,tbl_stream.stream_name');		$this->db->where('tbl_separate_student_course_work_result.is_deleted','0');        $this->db->where('tbl_separate_student_course_work_result.status','1');        //$this->db->where('tbl_separate_student_course_work_result.issue_status','1');		$this->db->where('tbl_separate_student_course_work_result.student_id',$this->input->post('id')); 		$this->db->join('tbl_separate_student','tbl_separate_student.id = tbl_separate_student_course_work_result.student_id');		$this->db->join('tbl_stream','tbl_stream.id = tbl_separate_student.stream_id'); 		$result = $this->db->get('tbl_separate_student_course_work_result');		$result = $result->row();		$result->bg_image = $this->Digitalocean_model->get_photo('images/marksheet-bg.jpeg');		$result->bg_image_2 = $this->Digitalocean_model->get_photo('images/asd.jpg');		$result->logo = $this->Digitalocean_model->get_photo('images/logo.png');		echo json_encode($result);	} 	public function get_course_work_marksheet_details_separate_student(){		$_GET = $_POST;		$this->db->where('is_deleted','0');		$this->db->where('status','1');		$this->db->where('result_id',$this->input->post('result_id'));		$result = $this->db->get('tbl_separate_course_work_result_details');     	$result = $result->result(); 		echo json_encode($result);	}	public function get_alumni_university_details(){		$_GET = $_POST; 		$result = $this->db->get('tbl_university_details');		$result = $result->row();		echo json_encode($result);	} 	public function get_my_alumni_password(){		$_GET = $_POST; 		$this->db->where('email',$this->input->post("email"));        $result = $this->db->get('tbl_separate_student');        $temp = $result->row();        if($result->num_rows() > 0){			$new_pass = mt_rand(1111,9999);;			$data=array('password' => $new_pass);        			$this->db->where('id',$temp->id);			$this->db->update('tbl_separate_student',$data);        			$this->load->library('email');			$this->email->from('noreply@gmail.com');			$this->email->to($this->input->post('email')); 			$this->email->subject('Forgot Password.');			$this->email->set_mailtype("html");			$message = "Dear $temp->student_name,<br>Your password has been reset. Now you can login with new password.<br>Your new login password is  $new_pass";			$this->email->to($this->input->post('email'));			$this->email->message($message); 			if($this->email->send()){  			}else{ 			}	 			echo json_encode(1);		}else{			echo json_encode(0);		}	} 	public function get_alumni_my_result(){		$_GET = $_POST; 		$this->db->where('is_deleted','0');			$this->db->where('status','1');			$this->db->where('created_on BETWEEN NOW() AND DATE_ADD(NOW(), INTERVAL 2 DAY)');			$this->db->where('student_id',$this->input->post('id'));			$this->db->order_by('year_sem','ASC');		$result = $this->db->get('tbl_separate_student_exam_results');		$result = $result->result();		echo json_encode($result);	}	public function get_alumni_show_my_result(){		$_GET = $_POST;  		$this->db->select('tbl_separate_student_exam_results.*,tbl_course.course_name,tbl_stream.stream_name,tbl_separate_student.student_name,tbl_separate_student.course_mode,tbl_separate_student.father_name,tbl_separate_student_exam_results.examination_month,tbl_separate_student_exam_results.examination_year'); 		$this->db->where('tbl_separate_student_exam_results.id',$this->input->post('result_id'));		$this->db->where('tbl_separate_student_exam_results.student_id',$this->input->post('id'));		$this->db->where('tbl_separate_student_exam_results.is_deleted','0');		$this->db->where('tbl_separate_student_exam_results.status','1');		$this->db->join('tbl_separate_student','tbl_separate_student.id = tbl_separate_student_exam_results.student_id');		$this->db->join('tbl_course','tbl_course.id = tbl_separate_student_exam_results.course_id');		$this->db->join('tbl_stream','tbl_stream.id = tbl_separate_student_exam_results.stream_id');		$result = $this->db->get('tbl_separate_student_exam_results');		$result = $result->row();		echo json_encode($result);	}	public function get_alumni_selected_subject_for_result(){		$_GET = $_POST;  		$this->db->select('tbl_separate_student_examination_result_details.*,tbl_subject.subject_name,tbl_subject.subject_code,tbl_subject.subject_type');		$this->db->where('tbl_separate_student_examination_result_details.is_deleted','0');		$this->db->where('tbl_separate_student_examination_result_details.status','1');		$this->db->where('tbl_separate_student_examination_result_details.result_id',$this->input->post('result_id'));		$this->db->join('tbl_subject','tbl_subject.id = tbl_separate_student_examination_result_details.subject_id');		$this->db->order_by('tbl_subject.subject_code','ASC');		$result = $this->db->get('tbl_separate_student_examination_result_details');		$result = $result->result();		echo json_encode($result);	}	public function get_alumni_provisional_degree(){		$_GET = $_POST;  		$this->db->select("tbl_separate_student_provisional_degree.*,tbl_separate_student.course_id,tbl_separate_student.student_name,tbl_separate_student.father_name,tbl_separate_student.enrollment_number,tbl_session.session_start_date,tbl_course.course_name,tbl_stream.stream_name");		$this->db->where("tbl_separate_student_provisional_degree.is_deleted","0");		$this->db->where("tbl_separate_student_provisional_degree.payment_status","1"); 		$this->db->where("tbl_separate_student_provisional_degree.student_id",$this->input->post("id")); 		$this->db->join("tbl_separate_student","tbl_separate_student.id = tbl_separate_student_provisional_degree.student_id");		$this->db->join("tbl_session","tbl_session.id = tbl_separate_student.session_id"); 		$this->db->join("tbl_course","tbl_course.id = tbl_separate_student.course_id");		$this->db->join("tbl_stream","tbl_stream.id = tbl_separate_student.stream_id"); 		$result = $this->db->get("tbl_separate_student_provisional_degree");		$result = $result->row();		if(!empty($result)){ 			$result->bg_image = $this->Digitalocean_model->get_photo('images/marksheet-bg-with-logo.jpeg');			$result->registrar = $this->Digitalocean_model->get_photo('images/signature_contrroler/sahakhan.png');			$result->seal = $this->Digitalocean_model->get_photo('images/btu-seal.png');		$result->logo = $this->Digitalocean_model->get_photo('images/logo.png');		}		echo json_encode($result);	}	public function get_alumni_year_duration(){		$_GET = $_POST;		$this->db->where('course',$this->input->post('course_id'));		$this->db->where('stream',$this->input->post('stream_id'));		$this->db->where('is_deleted','0');		$result = $this->db->get('tbl_course_stream_relation');		$result = $result->row();		echo json_encode($result);	}	public function get_student_division_for_degree(){		$_GET = $_POST;		$this->db->select("examination_year,internal_max_marks,internal_marks_obtained,external_max_marks,external_marks_obtained,created_on");		$this->db->where("is_deleted","0");		$this->db->where("status","1");		$this->db->where("result","0");		$this->db->where("student_id",$this->input->post("id"));		$this->db->order_by("id","DESC");		$result = $this->db->get("tbl_separate_student_exam_results")->result();		$total_marks = 0;		$gained_marks = 0;		foreach($result as $res){			$total_marks = $total_marks + $res->internal_max_marks + $res->external_max_marks;			$gained_marks = $gained_marks + $res->internal_marks_obtained + $res->external_marks_obtained;		}			$percentage = $total_marks == 0?0:($gained_marks/$total_marks)*100;		if($percentage >= 60){			$data["division"] = "First Division";		}else if($percentage < 60 & $percentage>= 45){			$data["division"] = "Second Division";		}else{			$data["division"] = "Third Division";		}		$data["date"] = $result[0]->examination_year;		echo json_encode($data);	} 	public function get_transfer_certificate(){		$_GET = $_POST;		$this->db->select("tbl_separate_student_transfer.*,tbl_separate_student.student_name,tbl_separate_student.father_name,tbl_separate_student.enrollment_number,tbl_separate_student.gender,tbl_course.course_name,tbl_stream.stream_name");		$this->db->where("tbl_separate_student_transfer.is_deleted","0");		$this->db->where('tbl_separate_student_transfer.payment_status',"1");		$this->db->where("tbl_separate_student_transfer.student_id",$this->input->post("id"));		$this->db->join("tbl_separate_student","tbl_separate_student.id = tbl_separate_student_transfer.student_id");		$this->db->join("tbl_course","tbl_course.id = tbl_separate_student.course_id");		$this->db->join("tbl_stream","tbl_stream.id = tbl_separate_student.stream_id");		$result = $this->db->get("tbl_separate_student_transfer")->row();		if(!empty($result)){			$result->bg_image = $this->Digitalocean_model->get_photo('images/marksheet-bg-with-logo.jpeg');			$result->spacial_officer = $this->Digitalocean_model->get_photo('images/spacial_officer.png');		}		echo json_encode($result);	}	public function get_charector_certificate(){		$_GET = $_POST;		$this->db->select('tbl_separate_student.*,tbl_student_sparate_charector.id as char_id,tbl_student_sparate_charector.last_month,tbl_student_sparate_charector.purpose_of,tbl_student_sparate_charector.transaction_id,tbl_student_sparate_charector.payment_status,tbl_student_sparate_charector.amount,tbl_student_sparate_charector.approve_status,tbl_student_sparate_charector.issue_date,tbl_student_sparate_charector.sent_for_print,tbl_student_sparate_charector.payment_date,tbl_student_sparate_charector.name_of_issuing_authority,tbl_course.course_name,tbl_stream.stream_name');		$this->db->where('tbl_student_sparate_charector.student_id',$this->input->post('id'));		$this->db->join('tbl_student_sparate_charector','tbl_student_sparate_charector.student_id = tbl_separate_student.id','left');		$this->db->join("tbl_course","tbl_course.id = tbl_separate_student.course_id");		$this->db->join("tbl_stream","tbl_stream.id = tbl_separate_student.stream_id");		$result = $this->db->get('tbl_separate_student');		$result = $result->row();		if(!empty($result)){			$result->bg_image = $this->Digitalocean_model->get_photo('images/new_marksheet-without.jpeg');			$result->logo = $this->Digitalocean_model->get_photo('images/only-logo.png');			$result->signature = $this->Digitalocean_model->get_photo('images/signature_contrroler/misal_sign.png');			$result->btu_seal = $this->Digitalocean_model->get_photo('images/btu-seal.png');		}		echo json_encode($result);	}	public function get_alumni_degree(){		$_GET = $_POST;		$this->db->select("tbl_separate_student_degree.*,tbl_separate_student.course_id,tbl_separate_student.stream_id,tbl_separate_student.student_name,tbl_separate_student.father_name,tbl_separate_student.enrollment_number,tbl_session.session_start_date,tbl_course.course_name,tbl_stream.stream_name");		$this->db->where("tbl_separate_student_degree.is_deleted","0");		$this->db->where("tbl_separate_student_degree.payment_status","1");		$this->db->where("tbl_separate_student_degree.student_id",$this->input->post("id"));  		$this->db->join("tbl_separate_student","tbl_separate_student.id = tbl_separate_student_degree.student_id");	