<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed'); class Common_controller extends CI_Controller {	public function __construct(){		parent::__construct();		$this->is_logged(); 	}	public function is_logged(){		if($this->session->userdata('admin_id') == ""){			redirect('erp-access');		}	}	public function validate_hidden_access(){		$this->form_validation->set_rules('password','password','required');		if($this->form_validation->run() === FALSE){			$this->load->view('admin/validate_hidden_access');		}else{			if($this->input->post('password') == "ABZB"){				$session = array(					'blended_validated' => '1'				);				$this->session->set_userdata($session);				$this->session->set_flashdata('success','Password validated successfully!');			}else{				$this->session->set_flashdata('message','Please enter valid password!');			}			redirect('validate_hidden_access');		}	}	public function logout_blend(){		$session = array(			'blended_validated' => '0'		);		$this->session->set_userdata($session);		$this->session->set_flashdata('success','Logout successfully!');		redirect('validate_hidden_access');	}}