<?php if (! defined('BASEPATH')) exit('No direct script access allowed');class Bvoc_model extends CI_Model{	public function get_all_bvoc_courses()	{		$_GET = $_POST;		$this->db->where('is_deleted', '0');		$this->db->where('status', '1');		$this->db->where('course_type', '2');		$result = $this->db->get('tbl_course');		$result = $result->result();		echo json_encode($result);	}	public function get_all_bvoc_streams()	{		$_GET = $_POST;		$this->db->select('tbl_course_stream_relation.*,tbl_faculty.faculty_name,tbl_course_type.course_type as course_type_name,tbl_course.course_name,tbl_course.course_type as tbl_course_type,tbl_course.course_link as tbl_course_course_link, tbl_stream.stream_name');		$this->db->where('tbl_course_stream_relation.is_deleted', '0');		$this->db->where('tbl_course_stream_relation.status', '1');		$this->db->where('tbl_course.is_deleted', '0');		$this->db->where('tbl_course.status', '1');		$this->db->where('tbl_course.course_type', '2');		$this->db->order_by('tbl_course_stream_relation.id', 'DESC');		$this->db->join('tbl_faculty', 'tbl_faculty.id = tbl_course_stream_relation.faculty','left');		$this->db->join('tbl_course_type', 'tbl_course_type.id = tbl_course_stream_relation.course_type','left');		$this->db->join('tbl_course', 'tbl_course.id = tbl_course_stream_relation.course','left');		$this->db->join('tbl_stream', 'tbl_stream.id = tbl_course_stream_relation.stream','left');		$result = $this->db->get('tbl_course_stream_relation');		$result = $result->result();		echo json_encode($result);	}	public function get_course_faq()	{		$_GET = $_POST;		$course = $this->input->post('id');		$this->db->where('is_deleted', '0');		$this->db->where('course_id', $course);		$result = $this->db->get('tbl_course_faq');		$result = $result->result();		echo json_encode($result);	}	public function get_bvoc_course_details()	{		$_GET = $_POST;		$id = $this->input->post('id');		$this->db->where('is_deleted', '0');		$this->db->where('status', '1');		$this->db->where('course_link', $id);		$this->db->where('course_type', '2');		$result = $this->db->get('tbl_course');		$result = $result->row();		echo json_encode($result);	}	public function get_all_bvoc_stream_details()	{		$_GET = $_POST;		$id = $this->input->post('id');		$this->db->select('tbl_course_stream_relation.*,tbl_faculty.faculty_name,tbl_course_type.course_type as course_type_name,tbl_course.course_name,tbl_course.course_type as tbl_course_type,tbl_course.course_link as tbl_course_course_link, tbl_stream.stream_name');		$this->db->where('tbl_course_stream_relation.is_deleted', '0');		$this->db->where('tbl_course_stream_relation.status', '1');		$this->db->where('tbl_course_stream_relation.course_link', $id);		$this->db->where('tbl_course.is_deleted', '0');		$this->db->where('tbl_course.status', '1');		$this->db->where('tbl_course.course_type', '2');		$this->db->order_by('tbl_course_stream_relation.id', 'DESC');		$this->db->join('tbl_faculty', 'tbl_faculty.id = tbl_course_stream_relation.faculty');		$this->db->join('tbl_course_type', 'tbl_course_type.id = tbl_course_stream_relation.course_type');		$this->db->join('tbl_course', 'tbl_course.id = tbl_course_stream_relation.course');		$this->db->join('tbl_stream', 'tbl_stream.id = tbl_course_stream_relation.stream');		$result = $this->db->get('tbl_course_stream_relation');		$result = $result->row();		echo json_encode($result);	}	public function get_bvoc_india_cities()	{		$_GET = $_POST;		$this->db->select('cities.*,states.name as state_name,countries.name as country_name');		$this->db->join('states', 'states.id = cities.state_id');		$this->db->join('countries', 'countries.id = states.country_id');		$this->db->where('countries.id', '101');		$this->db->order_by('states.name', 'ASC');		$result = $this->db->get('cities')->result();		echo json_encode($result);	}}