.append('<option value="">Select State</option>');			var opts = $.parseJSON(data);			var selecter = "<?php echo $stu_data['state']; ?>";			$.each(opts, function(i, d) {				if(selecter==d.id){					$('#state').append('<option value="' + d.id + '" selected>' + d.name + '</option>');				}else{					$('#state').append('<option value="' + d.id + '">' + d.name + '</option>');				}							});			$('#state').trigger('change');		},		 error: function(jqXHR, textStatus, errorThrown) {		   console.log(textStatus, errorThrown);		}	});	});$("#state").change(function(){  	$.ajax({		type: "POST",		url: "<?=base_url();?>admin/Ajax_controller/get_city_ajax",		data:{'state':$("#state").val()},		success: function(data){			$("#city").empty();			$('#city').append('<option value="">Select City</option>');			var opts = $.parseJSON(data);			var selecter = "<?php echo $stu_data['city']; ?>";			$.each(opts, function(i, d) {				if(selecter==d.id){			  		 $('#city').append('<option value="' + d.id + '" selected>' + d.name + '</option>');				}			   else{			   		$('#city').append('<option value="' + d.id + '">' + d.name + '</option>');			   }			});			$('#city').trigger('change');		},		 error: function(jqXHR, textStatus, errorThrown) {		   console.log(textStatus, errorThrown);		}	});	});$("#admission_form").validate({	rules: {				address: "required",				country: "required",				state: "required",				city: "required",				pincode: "required",				mobile:"required",		email:"required",			},	messages: {				address: "Enter correspondence address!",		country: "Please select country!",		state: "Please select state!",		city: "Please select city!",		pincode: "Please enter pincode!",		mobile:"Please enter Mobile!",		email:"Please enter Email!",	}, 	submitHandler: function(form){		form.submit();	} });   // changes$("#mycheckbox").change(function(){	alert('Yes');	if($("#mycheckbox").is(":checked")){		$("#text-data").show();	}else{		$("#text-data").hide();	}});</script>